// App.js
import React from 'react';
import {
  SafeAreaView,
  View,
  StyleSheet,
  Text,
  TextInput,
  Button,
  ScrollView
} from 'react-native';
import { AsyncStorage } from 'react-native';
import DayTable from '../../DayTable';
import LessonRow from '../../LessonRow'

class LessonsSchedule extends React.Component {
    render() {
      return (
        <SafeAreaView style={styles.container}>
          <ScrollView>
            <DayTable day={"Понедельник"} />
            <DayTable day={"Вторник"} />
            <DayTable day={"Среда"} />
            <DayTable day={"Четверг"} />
            <DayTable day={"Пятница"} />
            <DayTable day={"Суббота"} />
            <DayTable day={"Воскресенье"} />
          </ScrollView>
        </SafeAreaView>
      )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
    },
    item: { padding: 10 },
    name: { fontSize: 20 },
    description: { fontWeight: '600', marginTop: 4, color: 'rgba(0, 0, 0, .5)' },
    city: { marginTop: 4 }
  })

export default LessonsSchedule;
