// App.js
import React from 'react';
import { useState } from 'react';
import {
  SafeAreaView,
  View,
  StyleSheet,
  Text,
  TextInput,
  Button,
  ScrollView
} from 'react-native';
import { AsyncStorage } from 'react-native';
import LessonRow from '../LessonRow'



class DayTable extends React.Component {

  state = {
    listLessons: [
      <LessonRow uniq={this.props.day + "1"} key={1} />,
      <LessonRow uniq={this.props.day + "2"} key={2}/>,
      <LessonRow uniq={this.props.day + "3"} key={3}/>,
      <LessonRow uniq={this.props.day + "4"} key={4}/>,
      <LessonRow uniq={this.props.day + "5"} key={5} />,
      <LessonRow uniq={this.props.day + "6"} key={6}/>,
      <LessonRow uniq={this.props.day + "7"} key={7}/>
    ],

    count: 8,
  }


  render() {



    return (
    <View style={styles.container}>
      <ScrollView>
        <Text style={{
          alignSelf: 'center',
          fontSize: 18,
          marginBottom: 5,
          }}>
            День: {this.props.day}
            </Text>

            <View style={{
                alignItems: 'center',
                justifyContent: "center",
            }}>

              {this.state.listLessons}

              </View>

              <View style={{
                flexDirection: 'row',
                justifyContent: 'space-around'
              }}>
                {/* Add Button */}
                <Button title="Добавить урок" onPress={() => {
                  this.setState({
                    listLessons: this.state.listLessons.concat(<LessonRow uniq={this.props.day + this.state.count} key={this.state.count} />),
                  })
                  this.setState({
                    count: this.state.count + 1,
                  })
                  console.log(this.state.count)
                }} />

                {/* Delete Button */}
                <Button title="Удалить урок" color={'#ff1f29'} onPress={() => {
                  var array = [...this.state.listLessons];
                  if (array.length > 0) {
                    this.setState({
                      count: this.state.count - 1,
                    })

                    array.splice(array.length - 1, 1);

                    this.setState({
                      listLessons: array,
                    });
                    console.log(this.state.count)
                  }
                }}
                />
              </View>
        </ScrollView>
      </View>
      )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      backgroundColor: '#add8e6',
      marginBottom: 30,
    },
    item: { padding: 10 },
    name: { fontSize: 20 },
    description: { fontWeight: '600', marginTop: 4, color: 'rgba(0, 0, 0, .5)' },
    city: { marginTop: 4 }
  })

export default DayTable;
